import React, { useEffect, useState } from "react"
import FullCalendar from "@fullcalendar/react"
import dayGridPlugin from "@fullcalendar/daygrid"
import googleCalendarPlugin from "@fullcalendar/google-calendar"
import { Tooltip } from "bootstrap"
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap"

let tooltipInstance = null
// import { Toolti/p } from "bootstrap";

const CLIENT_ID =
  "800666244265-lto1ep2f97vs2ts40omitb1ko1ckjq8v.apps.googleusercontent.com"
const API_KEY = "AIzaSyCfAHxZMKrG-t6CBNDfU9DHyXb6fNtL2xw"

// Authorization scopes required by the API; multiple scopes can be
// included, separated by spaces.
const SCOPES =
  "https://www.googleapis.com/auth/userinfo.email https://www.googleapis.com/auth/calendar.readonly https://www.googleapis.com/auth/calendar.events https://www.googleapis.com/auth/calendar"

const GoogleCalendar = () => {
  const [login, setLogin] = useState(false)
  const [events, setEvents] = useState(null)
  const [eventTitle, setEventTitle] = useState(null)
  const [eventDesc, setEventDesc] = useState(null)
  const [modal, setModal] = useState(false)

  useEffect(() => {
    // Update the document title using the browser API
    const script = document.createElement("script")
    script.async = true
    script.defer = true
    script.src = "https://apis.google.com/js/api.js"

    document.body.appendChild(script)

    script.addEventListener("load", () => {
      if (window.gapi) handleClientLoad()
    })
  })
  const signin = () => {
    setLogin(true)
  }

  const openSignInPopup = () => {
    window.gapi.auth2.authorize(
      { client_id: CLIENT_ID, scope: SCOPES },
      (res) => {
        console.log(res)
        if (res) {
          console.log(window.gapi.client, res)

          if (res.access_token)
            localStorage.setItem("access_token", res.access_token)

          fetch(
            `https://www.googleapis.com/calendar/v3/users/me/calendarList?access_token=${res.access_token}`
          )
            .then((res) => res.json())
            .then((data) =>
              localStorage.setItem("calendarId", data.items[0].id)
            )

          window.gapi.client.load("calendar", "v3", listUpcomingEvents)
        }
      }
    )
  }

  /**
   *  On load, called to load the auth2 library and API client library.
   */
  const handleClientLoad = () => {
    window.gapi.load("client:auth2", initClient)
  }

  /**
   *  Initializes the API client library and sets up sign-in state
   *  listeners.
   */
  const initClient = () => {
    if (!localStorage.getItem("access_token")) {
      openSignInPopup()
    } else {
      fetch(
        `https://www.googleapis.com/calendar/v3/calendars/primary/events?key=${API_KEY}&orderBy=startTime&singleEvents=true`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("access_token")}`,
          },
        }
      )
        .then((res) => {
          if (res.status !== 401) {
            return res.json()
          } else {
            localStorage.removeItem("access_token")

            openSignInPopup()
          }
        })
        .then((data) => {
          if (data?.items) {
            // console.log("my data",data.items);
            setEvents(formatEvents(data.items))
          }
        })
    }
  }

  /**
   * Print the summary and start datetime/date of the next ten events in
   * the authorized user's calendar. If no events are found an
   * appropriate message is printed.
   */
  const listUpcomingEvents = () => {
    window.gapi.client.calendar.events
      .list({
        calendarId: "primary",
        // timeMin: new Date().toISOString(),
        showDeleted: true,
        singleEvents: true,
        // maxResults: 10,
        // orderBy: "startTime",
      })
      .then(function (response) {
        var events = response.result.items

        console.log(events)

        if (events.length > 0) {
          setEvents(formatEvents(events))
        }
      })
  }

  const formatEvents = (list) => {
    return list.map((item) => ({
      title: item.summary,
      disc: item.description,
      start: item.start.dateTime || item.start.date,
      end: item.end.dateTime || item.end.date,
    }))
  }

  // const inputdata =()=>{
  //     let dateStr = prompt('Enter a date in YYYY-MM-DD format');
  //     let date = new Date(dateStr + 'T00:00:00'); // will be in local time

  // }

  const addEvent = () => {
    if (window.gapi.client || localStorage.getItem("access_token")) {
      let today = new Date()
      var monthNames = [
        "January",
        "February",
        "March",
        "April",
        "May",
        "June",
        "July",
        "August",
        "September",
        "October",
        "November",
        "December",
      ]
      // let dateStr = prompt('Enter a start date ');
      var dateStr = prompt(
        "Please enter date.",
        today.getDate() +
          "-" +
          monthNames[today.getMonth()] +
          "-" +
          today.getFullYear()
      )
      let dateEnd = prompt("Enter a end date")
      let title = prompt("Enter title")
      let description = prompt("Enter disc")
      fetch(
        `https://www.googleapis.com/calendar/v3/calendars/primary/events?key=${API_KEY}&timeMax=${new Date(
          "Apr 01, 2021"
        ).toISOString()}&timeMin=${new Date("Apr 01, 2021").toISOString()}`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("access_token")}`,
          },
        }
      )
        .then((res) => res.json())
        .then((data) => console.log(data))
      fetch(
        `https://www.googleapis.com/calendar/v3/calendars/primary/events?key=${API_KEY}`,
        {
          method: "POST",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("access_token")}`,
          },
          body: JSON.stringify({
            end: {
              dateTime: new Date(dateEnd),
            },
            start: {
              dateTime: new Date(dateStr),
            },
            summary: title,
            description: description,
          }),
        }
      )
    }
  }

  const handelCancel = () => {
    setModal(false)
  }


  const handleEventClick = ({ event, el }) => {
    console.log(event)
    setModal(true)
    // eventTitle=event._def.title
    setEventTitle(event._def.title)
    setEventDesc(event._def.extendedProps.disc)
  }
  _def.extendedProps.disc
  // console.log(eventDesc,eventTitle)


  return (
    <>
      {login && <button onClick={addEvent}>Add event</button>}
      <button onClick={signin}>signen</button>
      <FullCalendar
        plugins={[dayGridPlugin, googleCalendarPlugin]}
        initialView="dayGridMonth"
        events={events}
        GoogleCalendarApikey={API_KEY}
        eventClick={handleEventClick}
      />
      <Modal
        isOpen={modal}
        className="model"
      >
            <ModalHeader>
            {eventTitle}
          </ModalHeader>
          <ModalBody>
            <div>
              <p>
              {eventDesc}
              </p>
            </div>
          </ModalBody>
          <ModalFooter>
            <Button color="primary">Do Something</Button>{" "}
            <Button color="secondary" onClick={handelCancel}>
              Cancel
            </Button>
          </ModalFooter>
      </Modal>
    </>
  )
}

export default GoogleCalendar
